
CREATE TABLE categories (
  id text NOT NULL,
  title text NOT NULL,
  description text NOT NULL,
  created_at timestamp with time zone DEFAULT now(),
  CONSTRAINT categories_pkey PRIMARY KEY (id)
);
CREATE TABLE levels (
  id text NOT NULL,
  title text NOT NULL,
  order_index integer NOT NULL,
  created_at timestamp with time zone DEFAULT now(),
  CONSTRAINT levels_pkey PRIMARY KEY (id)
);
CREATE TABLE questions (
  id uuid NOT NULL DEFAULT gen_random_uuid(),
  category_id text NOT NULL,
  level_id text NOT NULL,
  text text NOT NULL,
  options jsonb NOT NULL,
  correct_answer integer NOT NULL,
  created_at timestamp with time zone DEFAULT now(),
  explanation text NOT NULL DEFAULT ''::text,
  CONSTRAINT questions_pkey PRIMARY KEY (id),
  CONSTRAINT questions_category_id_fkey FOREIGN KEY (category_id) REFERENCES public.categories(id),
  CONSTRAINT questions_level_id_fkey FOREIGN KEY (level_id) REFERENCES public.levels(id)
);
-------------
